import React from 'react';
import { Link } from 'react-router-dom';
import '../stylesheets/characterDetail.scss';

const CharacterDetail = props => {
    const handleModalContentClick = ev => {
        ev.preventDefault();
    };
    return (
        <div className="modal">
            <div className="modal__dialog">
                <div className="modal__content" onClick={handleModalContentClick}>
                    <header className="modal__header">
                        <h2 className="modal__title">{props.name}</h2>
                        <Link to="/">
                            <span className="modal__close icon fas fa-times"></span>
                        </Link>
                    </header>
                    <section className="carddetail">
                        <div className="carddetail__content">
                            <img className="card__img" src={props.imageUrl} alt={props.name} />
                            <ul className="card__info">
                                <li>Specie: {props.specie}</li>
                                <li>Origin: {props.origin}</li>
                                <li>Episodes: {props.episode.length}</li>
                                <li>Status : {props.status}</li>
                            </ul>
                        </div>
                    </section>
                </div>
            </div>
        </div>
    )
}

export default CharacterDetail;
