import React, {useEffect, useState} from 'react';
import { Route, Switch} from 'react-router-dom';
import Filters from './Filters';
import CharacterList from './CharacterList';
import CharacterDetail from './CharacterDetail';
import CharacterCard from './CharacterDetail';
import getApiData from '../services/api';
import logo from '../images/rmlogo.png';

const App = () => {
  const [characters, setCharacters] = useState([]);

  useEffect(() => {
      getApiData().then(data => {
        setCharacters(data);
      });
  }, []);

  const renderCharacterDetail = (props) => {
    //const character = characters[0];
    const routeCharacterId = props.match.params.characterId;
    const character = characters.find(character => character.id === routeCharacterId);
    console.log(character);
    if (character) {
      return (
        <CharacterDetail

          imageUrl={character.image}
          name={character.name}
          specie={character.species}
        />,
        <CharacterCard

          imageUrl={character.image}
          name={character.name}
          specie={character.species}
          origin={character.origin.name}
          episode={character.episode}
          status={character.status}
        />
      );
    } else {
      return <p>Character not found</p>;
    }
  };

  //render() {
    return (
      <div>
        <img src={logo} className="title--logo" alt='Rick and Morty logo' />
        <h1 className="title--big">Rick and Morty</h1>
        <Filters />
        <CharacterList characters={characters} />
        <Switch>
          <Route path="/details/:characterId" render={renderCharacterDetail} />
        </Switch>
      </div>
    );
  //}
};

export default App;
