import React from 'react';
import Filters from './Filters';
import CharacterList from './CharacterList';
import CharacterDetail from './CharacterDetail';
import getApiData from '../services/api';


const App = () => class extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            products: []
        };
        this.renderProductDetail = this.renderProductDetail.bind(this);
    }

    componentDidMount() {
        getApiData().then(data => {
            this.setState({
                products: data
            });
        });
    }

    renderProductDetail = (props) => {
        console.log(props);
        const routeProductID = props.match.params.productId;
        const product = this.state.products.find(
            product => product.id === routeProductID
        );
        if (product) {
            return (
                <CharacterDetail
                    name={product.name}
                    imageUrl={product.imageUrl}
                    price={product.price}
                    description={product.description}
                    sizes={product.sizes}
                />
            );
        }
    };

    render() {
        //console.log(this.state);
        return (
            <div>
                <h1 className="title--big">Cat√°logo de camisetas</h1>
                <Filters />
                <CharacterList products={this.state.products} />

            </div>
        );
    }
};


/*
                <Switch>
                    <Route path="/product/:productId" render={this.renderProductDetail} />
                </Switch>
*/

export default App;
